import numpy as np

def get_prob_succ(p, n):
    return n * p * math.pow(1-p, n-1)

def get_prob_idle(p,n):
    return math.pow(1-p, n-1)


def get_utilization(p, n, z, k):
    a = 1.0/(z*k)
    b = 1 - get_prob_idle(p, n/k)
    return get_prob_succ(p, n/k) / (a + b)


